import React, { useState, useEffect } from 'react';

const ChatApp = () => {
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [senderId, setSenderId] = useState('');

  const handleSenderIdChange = (e) => {
    const inputValue = e.target.value;
    // Allow only digits (0-9)
    const sanitizedValue = inputValue.replace(/[^0-9]/g, '');
    setSenderId(sanitizedValue);
  };

  const sendMessage = async () => {
    // Check if required fields are filled
    if (!senderId || !newMessage) {
      alert('Sender ID and Message are required.');
      return;
    }

    // Send message to the backend
    const response = await fetch('/api/messages', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ sender: senderId, message: newMessage }),
    });

    const result = await response.json();
    console.log(result);

    // Clear the message input
    setNewMessage('');
  };

  useEffect(() => {
    // Fetch and display messages from the backend
    const fetchMessages = async () => {
      const response = await fetch('/api/messages');
      const result = await response.json();
      setMessages(result);
    };

    fetchMessages();
  }, []);

  return (
    <div className="chat-container">
      <div className="input-area">
        <label htmlFor="senderId" className="required-label">ID*</label>
        <input
          type="text"
          id="senderId"
          value={senderId}
          onChange={handleSenderIdChange}
          className="sender-input"
          placeholder="Enter your ID"
          required
        />
        <label htmlFor="newMessage" className="required-label">Message*</label>
        <textarea
          id="newMessage"
          value={newMessage}
          onChange={(e) => setNewMessage(e.target.value)}
          className="message-input"
          placeholder="Type your message..."
          required
        />
        <button onClick={sendMessage} className="send-button">Send</button>
      </div>
      <div className="chat-messages">
        {messages.map((msg) => (
          <div key={msg.id} className="message">
            <span className="sender-tag">{msg.sender}: </span>
            {msg.text}
          </div>
        ))}
      </div>
    </div>
  );
};

export default ChatApp;
